import React, { useState, useEffect } from 'react';

const ChosenCatagory = () => {
    const animalWords = ['chameleon', 'orangutan', 'turtle', 'dolphin', 'penguin', 'toad',
    'giraffe', 'elephant', 'zebra, beaver', 'snail', 'bunny', 'hippopotamus', 'toucan', 'alligator'];
    const foodWords = ['ketchup', 'strawberry', 'zucchini', 'doughnut', 'sandwich', 'spaghetti',
    'calzone', 'croissant', 'avocado, cauliflower', 'brownie', 'burrito', 'quesadilla', 'sundae', 'tomato'];

    const [catagory, setCatagory] = useState([]);

    const animalChange =(animalWords) => {
        const selectedCatagory = [...catagory, animalWords];
        selectedCatagory = animalWords;
        setCatagory(selectedCatagory);
    }
    const foodChange =(foodWords) => {
        const selectedCatagory = [...catagory, foodWords];
        selectedCatagory = foodWords;
        setCatagory(selectedCatagory);
    }

    return(
        <>
        <button onClick={() => animalChange(animalWords)}>
            Food
        </button>
        <h2></h2>
        <button onClick={() => foodChange(foodWords)}>
            Animals
        </button>
        </>
    )
}

export default ChosenCatagory;
